
$(document).ready(function(){$("#owl-demo").owlCarousel({navigation:true,slideSpeed:300,paginationSpeed:400,singleItem:true,pagination:false,navigationText:false});});$(function(){$(window).scroll(function(){if($(this).scrollTop()!=0){$('#toTop').fadeIn();}else{$('#toTop').fadeOut();}});$('#toTop').click(function(){$('body,html').animate({scrollTop:0},1000);});$('.smoothScroll').click(function(event){event.preventDefault();var href=$(this).attr('href');var target=$(href);var top=target.offset().top;$('html,body').animate({scrollTop:top},1000);});});